import React from 'react'
import './Featured.css';
import category_product from '../Assets/category'
import FeaturedCard from './FeaturedCard';

export const Category = () => {
  return (
    <div id="categories">
        <div className="title">
            <h2>Categories</h2>
        </div>
        <div className="categories-container container">
           {
            category_product.map((category,i) => {
                return <FeaturedCard key={i} image={category.image} name={category.name}/> 
            })
           }

        </div>

    </div>
  )
}
