import { useEffect, useState } from 'react'
import './Carousel.css'

const Carousel = ({images}) => {
    const [current, setCurrent] = useState(0);
    const [autoPlay, setAutoPlay] = useState(null);

    useEffect(() => {
        setTimeout(() => {slideRight()} ,2500)
    })

    const slideRight = () => {
        if(current === images.length - 1){
            setCurrent(0);
        }
        else{
            setCurrent(current+1)
        }
        
    }

    const slideLeft = () => {
        if(current === 0){
            setCurrent(images.length - 1);
        }
        else{
            setCurrent(current-1)
        }
    }
  return (
    <div className="carousel">
        <div className="carousel-wrapper">
            {images.map((image, index)=> {
            return <div key={index} className={index===current ? "carousel_cards carousel_cards-active" : "carousel_cards"}>
                <img src={image.image} alt=""/>
            </div>
        } )}
        <div className="carousel-arrow-left" onClick={slideLeft}>&lsaquo;</div>
        <div className="carousel-arrow-right" onClick={slideRight}>&rsaquo;</div>

        <div className="carousel-pagination">
            {images.map((_,index) => {
                return (
                    <div key={index}
                    className={index===current ? "pagination_dot pagination_dot-active" : "pagination_dot"}
                    onClick={() => setCurrent(index)}
                    ></div>
                )
            })}
        </div>
        </div>
    </div>
  )
}

export default Carousel
