import { useState } from 'react'
import './Carousel.css'

const Carousel = ({images}) => {
    const [current, setCurrent] = useState(0);

    const slideRight = () => {
        setCurrent(current+1)
    }

    const slideLeft = () => {
        setCurrent(current-1)
    }
  return (
    <div className="carousel">
        <div className="carousel-wrapper">
            {images.map((image, index)=> {
            return <div key={index} className={index===current ? "carousel_cards carousel_cards-active" : "carousel_cards"}>
                <img src={image.image} alt=""/>
            </div>
        } )}
        <div className="carousel-arrow-left" onClick={slideLeft}>&lsaquo;</div>
        <div className="carousel-arrow-right" onClick={slideRight}>&rsaquo;</div>
        </div>
    </div>
  )
}

export default Carousel
