import './App.css';
import { Navbar } from './Components/Navbar/Navbar';
import { Home } from './Pages/Home';
import { Products } from './Pages/Products';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { Cart } from './Pages/Cart';
import { LoginSignup } from './Pages/LoginSignup';
import { ProductDetail } from './Pages/ProductDetail';
import { Card } from './Components/Category Components/Card'
import products from './Components/Assets/all_product';
function App() {

  const [selectedCategory, setSelectedCategory] = useState(null);

  // ----------- Input Filter -----------
  const [query, setQuery] = useState("");

  const handleInputChange = (event) => {
    setQuery(event.target.value);
  };

  const filteredItems = products.filter(
    (product) => product.title.toLowerCase().indexOf(query.toLowerCase()) !== -1
  );

  // ----------- Radio Filtering -----------
  const handleChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  // ------------ Button Filtering -----------
  const handleClick = (event) => {
    setSelectedCategory(event.target.value);
  };

  function filteredData(products, selected, query) {
    let filteredProducts = products;

    // Filtering Input Items
    if (query) {
      filteredProducts = filteredItems;
    }

    // Applying selected filter
    if (selected) {
      filteredProducts = filteredProducts.filter(
        ({ category,newPrice, title }) =>
          category === selected ||
          newPrice === selected ||
          title === selected
      );
    }

    return filteredProducts.map(
      ({ image, title, category, prevPrice, newPrice }) => (
        <Card
          key={Math.random()}
          image={image}
          title={title}
          category={category}
          prevPrice={prevPrice}
          newPrice={newPrice}
        />
      )
    );
  }

  const result = filteredData(products, selectedCategory, query);

  return (
    <div>
      <BrowserRouter>
        <Navbar />
        <Routes>
          <Route path='/' exact element={<Home />} />
          <Route path='/products' element={<Products />}>
            <Route path='/products/mens' exact element={<Home />} />
          </Route>

          {/* <Route path="/productdata" element={<ProductDetail/>}/> */}
          {/* <Route path='/:productId' element={<ProductDetail/>}/> */}
          {/* </Route> */}
          <Route path='/cart' element={<Cart />} />
          <Route path='/login' element={<LoginSignup />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
