// create a Context
// provider 
// consumer to use the context hook 

import { createContext } from "react";


const Appcontext = createContext();

const AppProvider = ({children}) =>{
    return <Appcontext.Provider value="lovina">{children}</Appcontext.Provider>;
};

export {AppProvider}
