import './App.css';
import { Navbar } from './Components/Navbar/Navbar';
import { Hero } from './Components/Hero/Hero';
import { Featured } from './Components/Featured/Featured';
import { Home } from './Pages/Home';
import { ShopCategory } from './Pages/ShopCategory';
import {BrowserRouter, Routes, Route} from 'react-router-dom';
import { Cart } from './Pages/Cart';
import { LoginSignup } from './Pages/LoginSignup';
import { ProductDetail } from './Pages/ProductDetail';
import { Component, useState } from 'react';
import { Card } from './Components/Category Components/Card'
import products from './Components/Assets/all_product';
function App() {

  const [selectedCategory, setSelectedCategory] = useState(null)

  // Input Filters
  const [query, setQuery] = useState("")

  const handleInputChange = event =>{
    setQuery(event.target.value)
  }
  const filteredItems = products.filter(product => product.title.toLocaleLowerCase().indexOf(query.toLocaleLowerCase()!==-1))

  // Radio Filter
  const handleChange = event =>{
    setSelectedCategory(event.target.value)
  }

  //Button Filter
  const handleClick= event =>{
    setSelectedCategory(event.target.value)
  }

  function filterData(products,selected,query){
    let filteredProducts = products 

    // filtering input item
    if(query){
      filteredProducts = filteredItems
    }

    // Selected Filter
    if(selected){
      filteredProducts = filteredProducts.filter(({category,new_price,title}) => category ===selected || new_price ===selected || title === selected)
    }

    return filteredProducts.map(({id, title, category, image, prev_price, new_price}) => (
      <Card key={Math.random()} id={id} title={title} category={category} image={image} prev_price={prev_price} new_price={new_price}/>
    ))
  }

  const result = filterData(products,selectedCategory,query)
  return (
    <div>
       {/* <BrowserRouter> */}
        <Navbar/>


      <Hero/>
      <Featured/>
    </div>
  );
}

export default App;
